targets.expr:
sum(
  (1 - rate(node_cpu_seconds_total{job="node-exporter", mode="idle", instance=~"$instance", cluster=~"$Account"}[$__interval]))
/ ignoring(cpu) group_left
  count without (cpu)( node_cpu_seconds_total{job="node-exporter", mode="idle", instance=~"$instance", cluster=~"$Account"})
) by (instance) * on (instance) group_left(nodename) node_uname_info{nodename=~"$node", cluster=~"$Account"}
